class GoogleRecaptcha extends Component {
    loadRecaptcha = () => {
        console.log('loadRecaptcha');
    }

    componentDidMount() {
        console.log('componentDidMount');

        if (window) {
            grecaptcha.render('form-recaptcha', {
                'sitekey': '6LcH0-wUAAAAAK1AC3awIRgj4GBuIU03THxGywfJ',
                'callback': () => {
                    console.log('render callback');
                }
            });
        }
    }
   
    render() {

        return (
            <>
                <Head>
                    <script src="https://www.google.com/recaptcha/api.js?render=explicit"></script>
                </Head>
        
                {/* <div className="recaptcha__element"> */}
                    <div id="form-recaptcha" className="g-recaptcha" data-sitekey="6LcH0-wUAAAAAK1AC3awIRgj4GBuIU03THxGywfJ" data-callback="formSubmit"></div>
                {/* </div> */}
            </>
        )
    }
}

export default GoogleRecaptcha
