// }

// GoogleRecaptcha.getRecaptcha = () => {
//     console.log('getRecaptcha');
// }

// export default GoogleRecaptcha


class GoogleRecaptcha extends Component {
    loadRecaptcha = () => {
        console.log('loadRecaptcha');
    }

    componentDidMount() {
        console.log('componentDidMount');

        if (window) {
            (window as any).grecaptcha.render('form-recaptcha', {
                'sitekey': '6Ld8890UAAAAAHRc3NDL5nVCv_CiHP775U-C8aHX',
                'callback': () => null
            });
        }
    }
   
    render() {
        return (
            <>
                <Head>
                    <script src="https://www.google.com/recaptcha/api.js?render=explicit"></script>
                </Head>
        
                <div className="recaptcha__element">
                    Recaptcha goes here!
        
                    <div className="g-recaptcha" data-sitekey="XXXXXX" data-size="invisible" data-callback="formSubmit"></div>
                </div>
            </>
        )
    }
}

export default GoogleRecaptcha
