import styled from 'styled-components';

const GoogleRecaptchaSection = styled.section`
    font-family: Roboto, sans-serif;
    margin: 0 auto;
    max-width: 1024px;
    padding: 30px;
    width: 100%;
`;

const SectionTitle = styled.h1`
    font-size: 28px;
    font-weight: bold;
    margin: 0;
    padding: 0;
    text-align: center;
    text-transform: uppercase;
`;

const SectionForm = styled.form`
    margin: 20px 0;
    width: 480px;
`;

const FormFieldset = styled.form`
    border: none;
    margin: 10px 0;
    padding: 0;
`;

export { GoogleRecaptchaSection, SectionTitle, SectionForm, FormFieldset };
